const burger=document.querySelector(".header_burger"),header=document.querySelector(".header"),menu=document.querySelector(".header_menu_list");burger.addEventListener("click",e=>{burger.classList.toggle("active"),header.classList.toggle("active"),menu.classList.toggle("active")});for(var modal=document.getElementById("myModal"),btn=document.getElementsByClassName("btn"),span=document.getElementsByClassName("close")[0],i=0;i<btn.length;i++)btn[i].addEventListener("click",function(){openModalWindow()});function openModalWindow(){modal.style.display="block"}span.onclick=function(){modal.style.display="none"},window.onclick=function(e){e.target==modal&&(modal.style.display="none")};